mapper.update:
UPDATE member
SET 
	 member_name= #{memberName},
	 member_phone= #{memberPhone},
	 member_addr= #{memberAddr},
	 member_email= #{memberEmail}
WHERE 
	 member_id = #{memberId}
	 AND
	 member_pw = #{memberPw}

mapper.insert:
INSERT INTO 롤 잭스
	memberid(
			 member_id
	)
	VALUE(
			#{memberId}
	);

mapper.select:
SELECT
	member_id as memberId,
	member_pw as memberPw
FROM
	member
WHERE
	member_id = #{memberId} 
	AND
	member_email = #{memberEmail}
	AND
	member_name = #{memberName} 
	AND
	member_phone = #{memberPhone}

SELECT
	CONCAT(SUBSTRING(member_id , 1, 3), '******') as memberId
FROM
	member
WHERE
	member_email= #{memberEmail}
	AND
	member_name = #{memberName} 
	AND
	member_phone = #{memberPhone}
